WITH 
  aparse(workflow_job.started_at, '*-*-*T*:*:*Z') AS (year1, month1, day1, hour1, minute1, second1) , 
numeric(month1) - 1 as month1Num,floor((if(month1Num >= 2, numeric(year1), numeric(year1) - 1) - 1970)/4)* 24 * 60 * 60  as leaps1, 
if(month1Num = 1 ,31,
 if(month1Num = 2,59,
  if(month1Num = 3, 90,
   if(month1Num = 4,120,
    if(month1Num = 5,151, 
     if(month1Num = 6,181,
      if(month1Num = 7,212,
       if(month1Num = 8,243,
        if(month1Num = 9,273,
         if(month1Num = 10,304,
          if(month1Num = 11,334,
           if(month1Num = 12,365, 0
)))))))))))) as month1NumDays,
((numeric(year1) - 1970) * 365 * 24 * 60 * 60) 
+ (month1NumDays * 24 * 60 * 60) 
+ (numeric(day1) - 1) * 24 * 60 * 60 
+ ((numeric(hour1)) * 3600) 
+ (numeric(minute1)) * 60 
+ numeric(second1) 
+ leaps1 as start_time, 
  aparse(workflow_job.completed_at, '*-*-*T*:*:*Z') AS (year2, month2, day2, hour2, minute2, second2) , 
numeric(month2) - 1 as month2Num,floor((if(month2Num >= 2, numeric(year2), numeric(year2) - 1) - 1970)/4)* 24 * 60 * 60  as leaps2, 
if(month2Num = 1 ,31,
 if(month2Num = 2,59,
  if(month2Num = 3, 90,
   if(month2Num = 4,120,
    if(month2Num = 5,151, 
     if(month2Num = 6,181,
      if(month2Num = 7,212,
       if(month2Num = 8,243,
        if(month2Num = 9,273,
         if(month2Num = 10,304,
          if(month2Num = 11,334,
           if(month2Num = 12,365, 0
)))))))))))) as month2NumDays,
((numeric(year2) - 1970) * 365 * 24 * 60 * 60) 
+ (month2NumDays * 24 * 60 * 60) 
+ (numeric(day2) - 1) * 24 * 60 * 60 
+ ((numeric(hour2)) * 3600) 
+ (numeric(minute2)) * 60 
+ numeric(second2) 
+ leaps2 as finish_time

SELECT average(finish_time-start_time)/60 FROM Log 
WHERE workflow_job.status = 'completed'
FACET workflow_job.conclusion SINCE 1 MONTH AGO
